    --     CHAR(FLOOR(97 + (RAND() * 26))),
    --     CHAR(FLOOR(48 + (RAND() * 10)))
    -- ), 1, 6);
    -- Định dạng 'formatted_order_id' với ngày tháng hiện tại và order_id
    SET NEW.formatted_order_id = CONCAT(
        DATE_FORMAT(NOW(), '%d%m%y%H%i%S'), 
        order_increment
    );
END
$$
DELIMITER ;

